pragma solidity ^0.8.18;

import {Base64} from "base64-sol/base64.sol";

library TokenURIGen {
  function generateSVGTokenURI(
    string calldata name,
    string calldata description,
    string calldata image
  ) external pure returns (string memory) {
    return
      string(
        abi.encodePacked(
          "data:applicaton/json;base64,",
          Base64.encode(
            bytes(
              abi.encodePacked(
                '{"name": "',
                name,
                '", "description": "',
                description,
                '", "image": "data:image/svg+xml;base64,',
                image,
                '"}'
              )
            )
          )
        )
      );
  }

  function generateSVGTokenURI(
    string calldata name,
    string calldata description,
    string calldata image,
    string calldata attributes
  ) external pure returns (string memory) {
    return
      string(
        abi.encodePacked(
          "data:applicaton/json;base64,",
          Base64.encode(
            bytes(
              abi.encodePacked(
                '{"name": "',
                name,
                '", "description": "',
                description,
                '", "image": "data:image/svg+xml;base64,',
                image,
                '", "attributes": ',
                attributes,
                "}"
              )
            )
          )
        )
      );
  }

  function generateSVGTokenURI(
    string calldata name,
    string calldata description,
    string calldata external_url,
    string calldata image,
    string calldata attributes
  ) external pure returns (string memory) {
    return
      string(
        abi.encodePacked(
          "data:applicaton/json;base64,",
          Base64.encode(
            bytes(
              abi.encodePacked(
                '{"name": "',
                name,
                '", "description": "',
                description,
                '", "external_url": "',
                external_url,
                '", "image": "data:image/svg+xml;base64,',
                image,
                '", "attributes": ',
                attributes,
                "}"
              )
            )
          )
        )
      );
  }
}
","keccak256":"0x58db6115126e913c6eba19e5a598b39f4b858bf0488b5f94a807965467d43361","license":"MIT"},"contracts/libraries/utils/TypeCast.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

error TypeCast__toUint256_OutOfBounds();

library TypeCast {
  bytes16 internal constant ALPHABET = "0123456789abcdef";

  function toHexString(uint256 value, uint256 length) internal pure returns (string memory) {
    bytes memory buffer = new bytes(2 * length + 2);
    buffer[0] = "0";
    buffer[1] = "x";
    for (uint256 i = 2 * length + 1; i > 1; --i) {
      buffer[i] = ALPHABET[value & 0xf];
      value >>= 4;
    }
    return string(buffer);
  }

  function toColor(bytes3 value) internal pure returns (string memory) {
    bytes memory buffer = new bytes(6);
    for (uint256 i = 0; i < 3; i++) {
      buffer[i * 2 + 1] = ALPHABET[uint8(value[i]) & 0xf];
      buffer[i * 2] = ALPHABET[uint8(value[i] >> 4) & 0xf];
    }
    return string(buffer);
  }

  function toUint256(bytes memory _bytes) internal pure returns (uint256) {
    if (_bytes.length < 32) revert TypeCast__toUint256_OutOfBounds();
    uint256 tempUint;

    assembly {
      tempUint := mload(add(_bytes, 0x20))
    }

    return tempUint;
  }
}
